#import "AZLPhotoBrowserTheme.h"
#import <AZLExtend/AZLExtend.h>

@implementation AZLPhotoBrowserTheme

- (instancetype)init{
    if (self = [super init]) {
        _barBackgroundColor = [UIColor darkGrayColor];
        _barTintColor = [UIColor whiteColor];
        
        _enableBackgroundColor = [UIColor colorWithRed:50.0f/255 green:205.0f/255 blue:50.0f/255 alpha:1];
        _enableTextColor = [UIColor whiteColor];
        _disableBackgroundColor = [UIColor lightGrayColor];
        _disableTextColor = [UIColor whiteColor];
        
        _sepLineColor = [UIColor lightGrayColor];
        
        _backgroundColor = [UIColor blackColor];
        _textColor = [UIColor whiteColor];
        
        //默認文字
        _finishString = @"完成";
        _allString = @"全部";
        _animateString = @"动图";
        _cancelString = @"取消";
        //_moreThanMaxAlertString = @"你最多只能选择9张照片";
        _moreThanMaxAlertConfirmString = @"我知道了";
        _photoAuthAlertTitleString = @"访问相册";
        _photoAuthAlertContentString = @"您还没有打开相册权限";
        _photoAuthOpenString = @"去打开";
        _photoSaveImageString = @"保存到相冊";
        _editString = @"编辑";
        [self setup];
    }
    return self;
}

- (void)setup{
    NSString *backImageStr = @"";
    NSData *backImagedata = [[NSData alloc] initWithBase64EncodedString:backImageStr options:0];
    self.backImage = [[UIImage alloc] initWithData:backImagedata scale:2];
    
    NSString *undoImageStr = @"";
    NSData *undoImagedata = [[NSData alloc] initWithBase64EncodedString:undoImageStr options:0];
    self.undoImage = [[UIImage alloc] initWithData:undoImagedata scale:2];
    self.redoImage = [self.undoImage azl_imageFromMirrorHorizon];
}

@end
